define('ENCRYPTION_KEY', 'votre_cle_de_32_caracteres'); // Clé de 256 bits

function encrypt($data) {
    $key = ENCRYPTION_KEY;
    $iv = openssl_random_pseudo_bytes(16); // Générer un IV de 16 bytes
    $encrypted = openssl_encrypt($data, 'aes-256-cbc', $key, 0, $iv);
    return base64_encode($encrypted . '::' . $iv); // Encodage base64 pour transmission sécurisée
}

function decrypt($data) {
    $key = ENCRYPTION_KEY;
    list($encrypted_data, $iv) = explode('::', base64_decode($data), 2); // Séparer les données chiffrées de l'IV
    return openssl_decrypt($encrypted_data, 'aes-256-cbc', $key, 0, $iv);
}

// Chiffrer le paramètre
$param = 'valeur_a_chiffrer';
$encrypted_param = encrypt($param);
$url = 'votrepage.php?param=' . urlencode($encrypted_param);
echo 'URL avec paramètre chiffré : ' . $url . PHP_EOL;

// Déchiffrer le paramètre dans votrepage.php
if (isset($_GET['param'])) {
    $encrypted_param = $_GET['param'];
    $decrypted_param = decrypt($encrypted_param);
    echo 'Paramètre déchiffré : ' . $decrypted_param;
}
